import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Search from './Search'


export default function ChooseCat () {
    let [cat, updateCat] = useState([])

    const apiCall = async () => {
        const data = await axios(`https://api.thecatapi.com/v1/images/search?mime_types=jpg,png&api_key=${process.env.REACT_APP_API_KEY_CAT}`)
        console.log(data.data[0].url)
        updateCat(data.data[0].url)
        }

    useEffect(() => {
        apiCall()
    }, [])



    return (<>
            <div className="random-cat-box">
            <h2>Choose a Cat</h2>
            <Search />
            <button onClick={apiCall}>Random Image</button>
            <div className="cat-image"><img src={cat} alt="cat"></img></div>
            </div>

        </>)

}
