import React, { Component } from 'react'
import { useState } from 'react'


export default function AddBorder () {
    let [border, updateBorder] = useState("")


    const handleClick = (e) => {
        e.preventDefault()
        updateBorder({
            border: '10px solid magenta'
        })
    }


    return (<>
    <button onClick={handleClick}>Add border</button>

    </>)
}
