import { useState, useEffect } from 'react'
import { Route } from 'react-router-dom'

import axios from 'axios'

export default function CatGif () {
        let [cat, updateCat] = useState([])

        const apiCall = async () => {
            const data = await axios(`https://api.thecatapi.com/v1/images/search?mime_types=gif&api_key=${process.env.REACT_APP_API_KEY_CAT}`)
            console.log(data.data[0].url)
            updateCat(data.data[0].url)
            }

        useEffect(() => {
            apiCall()
        }, [])


