import React, { Component } from 'react'
import { useState } from 'react'

export default function AddCaption(props) {
    let [caption, updateCaption] = useState("")
    //let [captionSubmitted, handleClick] = useState("")

    let handleClick = (e) => {
        e.preventDefault()
        //captionSubmitted = caption
        //console.log(captionSubmitted)
    }

    let resetForm = () => {
        updateCaption({input: ""})
    }

//const completeCaption = 

    return (<>
        <div className="add-caption">
            <p>ADD A CAPTION</p>
            <form onSubmit={handleClick} onReset={resetForm}> 
                <input 
                    type='text'
                    placeholder="caption" 
                    value={caption}
                    onChange={e => updateCaption(e.target.value)}
                    />
                <button>ADD</button>
            </form>
            <p>{caption}</p>
        </div>
   </>)

}
