import React, { Component } from 'react'
import { useState } from 'react'

export default function AddCaption(props) {
    let [caption, updateCaption] = useState("")

    const handleClick = (e) => {
        e.preventDefault()
        if (!caption) return
        addCaption (caption)
        updateCaption("")
    }
    console.log()

    const addCaption = text => {
        const newCaption = [{text}]
        updateCaption(newCaption)
    }

    return (<>
        <div className="add-caption">
            <p>ADD A CAPTION</p>
            <input 
                type='text'
                placeholder="caption" 
                value={caption}
                onChange={e => updateCaption(e.target.value)}
                />
            <button onClick={handleClick}>ADD</button>
            <p>{updateCaption}</p>
        </div>
   </>)

}
