import React, {Component} from 'react'
import { useState, useEffect } from 'react'
import { Route } from 'react-router-dom'

import axios from 'axios'

export default function CatGif () {
        let [catSearch, updateCatSearch] = useState([])

        const apiCall = async () => {
            const data = await axios(`https://api.thecatapi.com/v1/images/search?mime_types=gif&api_key=${process.env.REACT_APP_API_KEY}`)
            console.log(data.data[0].url)
            updateCatSearch(data.data[0].url)
            }

        useEffect(() => {
            apiCall()
        }, [])


    return (<>
        <button onClick={apiCall}>Random GIF</button>
        <div className="cat-image-gif"><img src={catSearch} alt="cat-gif"></img></div>
    </>)
}






