import React, { useState, useEffect } from 'react'
import axios from 'axios'
import DogGif from './DogGif'


export default function ChooseDog () {
    let [dog, updateDog] = useState([])

    const apiCall = async () => {
        const data = await axios(`https://api.thedogapi.com/v1/images/search?mime_types=jpg,png&api_key=${process.env.REACT_APP_API_KEY_DOG}`)
        console.log(data.data[0].url)
        updateDog(data.data[0].url)
        }

    useEffect(() => {
        apiCall()
    }, [])



    return (<>
            <div className="random-dog-box">
            <h2>Choose a Dog</h2>
            <DogGif />
            <button onClick={apiCall}>Random Image</button>
            <div className="dog-image"><img src={dog} alt="dog"></img></div>
            </div>

        </>)

}
